import React, { useState } from 'react';
import { useTheme } from 'styled-components';
import { Fontisto } from '@expo/vector-icons';
import { ActivityIndicator, Alert, Modal, View } from 'react-native';

import { useAuth } from '../../hooks/useAuth';

import LoginBannerImg from '../../assets/images/login.svg';
import LogoImg from '../../assets/images/logo.svg';

import {
  Container,
  Content,
  LoginBanner,
  LoginInfo,
  Header,
  Partner,
  Description,
  SignInButton,
  SignInButtonIcon,
  SignInButtonText,
  Loading,
} from './styles';

export function SignIn() {
  const { signIn, isLoggingIn } = useAuth();
  const theme = useTheme();

  const handlePressoLogin = async () => {
    try {
      await signIn();
    } catch (error) {
      console.log(error);
      Alert.alert('Nao foi possível entra.\nTente mais tarde.');
    }
  };

  const signInButtonProps = {
    onPress: handlePressoLogin,
  };

  return (
    <Container
      from={{
        opacity: 0,
        scale: 0.9,
      }}
      animate={{
        opacity: 1,
        scale: 1,
      }}
      exit={{
        opacity: 0,
        scale: 0.9,
      }}
    >
      <Content>
        <LoginBanner>
          <LoginBannerImg width="100%" />
        </LoginBanner>

        <LoginInfo>
          <Header>
            <LogoImg />
            <Partner>by twitch</Partner>
          </Header>

          <Description>
            Veja dados{'\n'}
            interessantes sobre{'\n'}o mundo da Twitch
          </Description>

          <SignInButton {...signInButtonProps}>
            {isLoggingIn ? (
              <Loading />
            ) : (
              <>
                <SignInButtonIcon>
                  <Fontisto
                    name="twitch"
                    size={28}
                    color={theme.colors.white}
                  />
                </SignInButtonIcon>

                <SignInButtonText>Entrar com Twitch</SignInButtonText>
              </>
            )}
          </SignInButton>
        </LoginInfo>
      </Content>

      <Modal
        animationType="fade"
        visible={isLoggingIn}
        statusBarTranslucent
        transparent
      >
        <View style={{ flex: 1, backgroundColor: 'rgba(14, 14, 16, 0.5)' }} />
      </Modal>
    </Container>
  );
}
